
public class FloatingPointLiteral {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double a = 128.625454;//Normal floating point literal
		double b = 1.28625454e+2;// Exponancial floating point literal
		System.out.println(a);
		System.out.println(b);
		
		double c = 1_2_8.6_25_454;//double
		c = 1_2_8.6_25_454;
		System.out.println(c);
		double x = 000002.3;//this zeros are ignored
		System.out.println(x);
		
		

	}

}
